// Enhanced multi-page build script with comprehensive SEO including index.html
import { writeFileSync, mkdirSync, existsSync, readFileSync } from "node:fs";
import { join, dirname, basename } from "node:path";
import { fileURLToPath } from "node:url";
import { generateSEOData, generateMetaTags, getSitemapData } from './seo-generator';

// Read Vite manifest to get compiled asset names dynamically
function getCompiledAssets(): { js: string, css: string } {
  const manifestPath = 'dist/.vite/manifest.json';
  const indexHtmlPath = 'dist/index.html';

  let jsAsset = '/assets/main.js'; // fallback
  let cssAsset = '/assets/main.css'; // fallback

  try {
    // Try to read from built index.html first (most reliable)
    if (existsSync(indexHtmlPath)) {
      const indexContent = readFileSync(indexHtmlPath, 'utf8');

      // Extract JS asset
      const jsMatch = indexContent.match(/<script[^>]+src="([^"]+\.js)"[^>]*>/);
      if (jsMatch) jsAsset = jsMatch[1];

      // Extract CSS asset  
      const cssMatch = indexContent.match(/<link[^>]+href="([^"]+\.css)"[^>]*>/);
      if (cssMatch) cssAsset = cssMatch[1];
    }
  } catch (error) {
    console.warn('Warning: Could not read compiled assets, using fallbacks');
  }

  return { js: jsAsset, css: cssAsset };
}

// NEW: Function to inject SEO into existing index.html
function injectSEOIntoIndexHtml(): void {
  const indexPath = join('dist', 'index.html');

  if (!existsSync(indexPath)) {
    console.warn('‚ö†Ô∏è index.html not found, skipping SEO injection');
    return;
  }

  try {
    // Read the existing index.html
    let indexContent = readFileSync(indexPath, 'utf8');

    // Generate SEO data for homepage
    const seoData = generateSEOData('/', undefined);
    const metaTags = generateMetaTags(seoData);

    // Find the closing </head> tag and inject SEO before it
    const headCloseTag = '</head>';
    const headCloseIndex = indexContent.indexOf(headCloseTag);

    if (headCloseIndex === -1) {
      console.warn('‚ö†Ô∏è Could not find </head> tag in index.html');
      return;
    }

    // Prepare SEO injection with proper spacing
    const seoInjection = `
  <!-- SEO Meta Tags - Injected by build script -->
  ${metaTags}

  <!-- Initial page route for React router -->
  <script>
    window.__INITIAL_ROUTE__ = '/';
    window.__SEO_DATA__ = ${JSON.stringify(seoData)};
  </script>

  <!-- Additional SEO enhancements -->
  <meta name="robots" content="index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1">
  <meta name="googlebot" content="index, follow, max-video-preview:-1, max-image-preview:large, max-snippet:-1">

  <!-- Geographic meta tags -->
  <meta name="geo.region" content="PH">
  <meta name="geo.country" content="Philippines">
  <meta name="geo.placename" content="Philippines">

  <!-- Language and locale -->
  <meta http-equiv="content-language" content="en">
  <meta name="language" content="English">

  <!-- DNS prefetch for performance -->
  <link rel="dns-prefetch" href="//tepasolutions.asia">
  <link rel="dns-prefetch" href="//www.google-analytics.com">
  <link rel="dns-prefetch" href="//fonts.googleapis.com">

`;

    // Insert SEO tags before closing head tag
    const updatedContent = indexContent.slice(0, headCloseIndex) + seoInjection + indexContent.slice(headCloseIndex);

    // Also add SEO-friendly content in body if not present
    if (!updatedContent.includes('seo-content')) {
      const bodyOpenMatch = updatedContent.match(/<body[^>]*>/);
      if (bodyOpenMatch) {
        const bodyOpenEnd = updatedContent.indexOf('>', bodyOpenMatch.index) + 1;
        const seoContentHtml = `
    <!-- SEO-friendly content preview for crawlers -->
    <div class="seo-content" style="position: absolute; left: -9999px; top: -9999px;">
      <h1>${seoData.title}</h1>
      <p>${seoData.description}</p>
      <div itemscope itemtype="https://schema.org/Organization">
        <span itemprop="name">Tepa Solutions</span>
        <span itemprop="description">Leading digital solutions provider in the Philippines</span>
        <span itemprop="address" itemscope itemtype="https://schema.org/PostalAddress">
          <span itemprop="addressCountry">Philippines</span>
        </span>
      </div>
    </div>
`;

        const finalContent = updatedContent.slice(0, bodyOpenEnd) + seoContentHtml + updatedContent.slice(bodyOpenEnd);

        // Write the enhanced index.html
        writeFileSync(indexPath, finalContent, 'utf8');
        console.log('‚úÖ SEO injection complete: index.html enhanced with comprehensive SEO');
        return;
      }
    }

    // Fallback: just write the head enhancements
    writeFileSync(indexPath, updatedContent, 'utf8');
    console.log('‚úÖ SEO injection complete: index.html enhanced with meta tags');

  } catch (error) {
    console.error('‚ùå Error injecting SEO into index.html:', error);
  }
}

// Define all routes that need individual HTML files
const routes = [
  { path: '/', file: 'index.html' },
  { path: '/business-automation', file: 'business-automation.html' },
