  <link rel="dns-prefetch" href="//fonts.googleapis.com">

  <!-- Preload critical resources -->
  <link rel="stylesheet" href="${assets.css}">
  <link rel="preload" href="${assets.js}" as="script" crossorigin>

  <!-- Initial page route for React router -->
  <script>
