@layer components {
  .navigation-dropdown {
    max-width: 100%;
    overflow: hidden;
  }

  @media (width >= 768px) {
    .navigation-dropdown {
      max-width: min(100vw - 4rem, 800px);
    }
  }

  @media (width <= 767px) {
    .navigation-dropdown {
      width: 100%;
      max-width: none;
    }
  }

  @media (pointer: coarse) {
    .navigation-dropdown button {
      min-height: 44px;
      padding-top: .75rem;
      padding-bottom: .75rem;
    }
  }

  @media (prefers-contrast: high) {
    .navigation-dropdown button {
      border: 1px solid #0000;
    }

    .navigation-dropdown button:hover, .navigation-dropdown button:focus {
      border-color: currentColor;
    }
  }

  @media (prefers-reduced-motion: reduce) {
    .navigation-dropdown * {
      transition-duration: .1s !important;
      animation-duration: .1s !important;
    }
  }

  .navigation-dropdown button:focus {
    outline: 2px solid var(--color-ring);
    outline-offset: 2px;
  }

  .navigation-submenu {
    scrollbar-width: thin;
    scrollbar-color: var(--color-border) transparent;
    max-height: calc(100vh - 8rem);
    overflow-y: auto;
  }

  .navigation-submenu::-webkit-scrollbar {
    width: 4px;
  }

  .navigation-submenu::-webkit-scrollbar-track {
    background: none;
  }

  .navigation-submenu::-webkit-scrollbar-thumb {
    background: var(--color-border);
    border-radius: 2px;
  }
}

