import { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Menu, ChevronDown } from 'lucide-react';
import { Button } from './ui/button';
import { Logo } from './OtherComponent/Logo';
import { navItems } from './data/navigation-data';
import { MobileNavigationSheet } from './OtherComponent/MobileNavigationSheet';
import { NavigationDropdown } from './NavigationDropdown';

interface NavigationProps {
  navigate?: (path: string) => void;
  currentPath?: string;
}

export function Navigation({ navigate, currentPath }: NavigationProps) {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeDesktopDropdown, setActiveDesktopDropdown] = useState<string | null>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Handle clicks outside dropdown
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setActiveDesktopDropdown(null);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const scrollToSection = (href: string) => {
    if (href === "#") {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else {
      document.querySelector(href)?.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const handleServiceAction = (action: string, target: string) => {
    if (action === "navigate") {
      if (navigate) {
        // Use the new routing system
        const pathMap: Record<string, string> = {
          'home': '/',
          'automation': '/business-automation',
          'app-dev': '/mobile-app-development',
          'web-app': '/web-application-development',
          'web-dev': '/website-development',
          'seo': '/seo-services',
          'automation-sales': '/business-automation/sales-process-automation',
          'automation-marketing': '/business-automation/marketing-automation',
          'automation-support': '/business-automation/customer-support-automation',
          'automation-hr': '/business-automation/hr-automation',
          'automation-finance': '/business-automation/finance-automation',
          'automation-inventory': '/business-automation/inventory-management-automation',
          'about-company': '/learn-about-tepa',
          'careers': '/careers',
          'events': '/events',
          'articles': '/articles',
          'investors': '/investors',
          'who-we-serve': '/who-we-serve',
          'contact-sales': '/contact-us/sales',
          'contact-support': '/contact-us/support',
          'contact-volunteer': '/volunteer-with-us',
          'contact-event-host': '/contact-us/event-hosting',
          'contact-investor': '/contact-us/investors'
        };

        const path = pathMap[target] || `/${target}`;
        navigate(path);
      } else {
        // Fallback to old event system for backward compatibility
        if (target === "automation") {
          window.dispatchEvent(new CustomEvent('navigateToAutomation'));
        } else if (target.startsWith('automation-')) {
          window.dispatchEvent(new CustomEvent('navigateToAutomation', { detail: target }));
        } else {
          window.dispatchEvent(new CustomEvent('navigateToService', { detail: target }));
        }
      }
    }
    setActiveDesktopDropdown(null);
  };

  const handleLogoClick = () => {
    if (navigate) {
      navigate('/');
    } else {
      window.dispatchEvent(new CustomEvent('navigateToService', { detail: 'home' }));
    }
  };

  const handleContactClick = () => {
    if (currentPath === '/') {
      scrollToSection("#contact");
    } else if (navigate) {
      navigate('/contact-us/sales');
    } else {
      scrollToSection("#contact");
    }
  };

  // Desktop dropdown handlers with hover delay
  const handleDesktopDropdownEnter = (label: string) => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setActiveDesktopDropdown(label);
  };

  const handleDesktopDropdownLeave = () => {
    timeoutRef.current = setTimeout(() => {
      setActiveDesktopDropdown(null);
    }, 150); // Small delay to allow moving to dropdown
  };

  const handleDropdownMouseEnter = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  };

  const handleDropdownMouseLeave = () => {
    setActiveDesktopDropdown(null);
  };

  return (
    <>
      <motion.nav
               className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
          isScrolled 
            ? 'bg-background/80 backdrop-blur-lg border-b shadow-sm' 
            : 'bg-transparent'
        }`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.6, ease: "easeOut" }}
      >
        <div className="container mx-auto px-6 max-w-7xl">
          <div className="flex items-center justify-between h-16">
            {/* Logo */}
            <Logo 
              size="md" 
              showText={true} 
              onClick={handleLogoClick}
            />

            {/* Desktop Navigation */}
            <div className="hidden md:flex items-center gap-8">
              {navItems.map((item) => (
                <div key={item.label} className="relative">
                  {item.hasDropdown ? (
                    <div
                      ref={activeDesktopDropdown === item.label ? dropdownRef : null}
                      onMouseEnter={() => handleDesktopDropdownEnter(item.label)}
                      onMouseLeave={handleDesktopDropdownLeave}
                    >
                      <button
                        className="flex items-center gap-1 px-4 py-2 hover:text-primary transition-colors"
                      >
                        {item.label}
                        <ChevronDown className="w-3 h-3" />
                      </button>

                      {/* Desktop Dropdown - Positioned absolutely below nav */}
                      <AnimatePresence>
                        {activeDesktopDropdown === item.label && (
                          <div 
                            className="absolute top-full left-1/2 transform -translate-x-1/2 mt-2 z-[10000]"
                            onMouseEnter={handleDropdownMouseEnter}
                            onMouseLeave={handleDropdownMouseLeave}
                          >
                            <motion.div
                              className="bg-background/95 backdrop-blur-lg border rounded-xl shadow-2xl overflow-hidden"
                              initial={{ opacity: 0, y: 10, scale: 0.95 }}
                              animate={{ opacity: 1, y: 0, scale: 1 }}
                              exit={{ opacity: 0, y: 10, scale: 0.95 }}
                              transition={{ duration: 0.2 }}
                            >
                              <NavigationDropdown
                                items={item.dropdownItems || []}
                                onAction={handleServiceAction}
                                isMobile={false}
                              />
                            </motion.div>
                          </div>
                        )}
                      </AnimatePresence>
                    </div>
                  ) : (
                    <button
                      onClick={() => scrollToSection(item.href)}
                      className="flex items-center gap-2 px-4 py-2 hover:text-primary transition-colors"
                    >
                      {item.label}
                    </button>
                  )}
                </div>
              ))}

              {/* CTA Button */}
              <Button 
                className="bg-primary hover:bg-primary/90"
                onClick={handleContactClick}
              >
                Contact Us
              </Button>
            </div>

            {/* Mobile Menu Button */}
            <Button
              variant="ghost"
              size="icon"
              className="md:hidden"
              onClick={() => setIsMobileMenuOpen(true)}
            >
              <Menu className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </motion.nav>

      {/* Mobile Navigation Sheet */}
      <MobileNavigationSheet
        isOpen={isMobileMenuOpen}
        onClose={() => setIsMobileMenuOpen(false)}
        onAction={handleServiceAction}
      />
    </>
  );
}

src/components/NavigationDropdown.tsx.content:
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronRight } from 'lucide-react';
import { useState, useRef, useCallback } from 'react';

export interface DropdownItem {
  icon: React.ComponentType<{ className?: string }>;
  label: string;
  description: string;
  action: string;
  target: string;
  subItems?: DropdownItem[];
}

interface NavigationDropdownProps {
  items: DropdownItem[];
  onAction: (action: string, target: string) => void;
  isMobile: boolean;
}

interface NavigationDropdownProps {
  items: DropdownItem[];
  onAction: (action: string, target: string) => void;
  isMobile: boolean;
  mobileSubMenu?: string | null;
  onMobileSubMenuToggle?: (target: string | null) => void;
}

export function NavigationDropdown({
  items,
  onAction,
  isMobile,
  mobileSubMenu = null,
  onMobileSubMenuToggle
}: NavigationDropdownProps) {
  // Desktop hover state only
  const [desktopHoveredItem, setDesktopHoveredItem] = useState<string | null>(null);

  const containerRef = useRef<HTMLDivElement>(null);

  // Desktop hover handlers
  const handleDesktopItemHover = useCallback((item: DropdownItem) => {
    if (item.subItems && item.action === "expand") {
      setDesktopHoveredItem(item.target);
    }
  }, []);

  const handleDesktopItemLeave = useCallback(() => {
    setDesktopHoveredItem(null);
  }, []);

  const handleItemClick = useCallback((item: DropdownItem) => {
    if (item.action === "expand" && item.subItems) {
      if (isMobile && onMobileSubMenuToggle) {
        // Mobile: toggle submenu via parent component
        const newTarget = mobileSubMenu === item.target ? null : item.target;
        onMobileSubMenuToggle(newTarget);
      }
      // Desktop: no click action for expandable items (hover handled)
    } else {
      // Regular navigation action
      onAction(item.action, item.target);
    }
  }, [isMobile, onAction, mobileSubMenu, onMobileSubMenuToggle]);

  // Mobile Component - Nested submenu under parent
if (isMobile) {
  return (
    <div className="p-2 space-y-1">
      {items.map((item, index) => (
        <div key={item.label}>
          {item.subItems ? (
            <>
              {/* Mobile Parent Item with submenu */}
              <div className="flex items-center justify-between">
                <button
                  onClick={() => onAction("navigate", item.target)}
                  className="flex items-center gap-3 p-3 text-left hover:bg-accent rounded-lg transition-colors flex-1 border-r border-border/30"
                  style={{ minWidth: 0 }}
                >
                  <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0">
                    <item.icon className="w-4 h-4 text-primary" />
                  </div>
                  <div>
                    <div className="font-medium text-foreground text-sm">
                      {item.label}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {item.description}
                    </div>
                  </div>
                </button>
                <button
                  onClick={() => {
                    if (onMobileSubMenuToggle) {
                      onMobileSubMenuToggle(mobileSubMenu === item.target ? null : item.target);
                    }
                  }}
                  className="p-3 hover:bg-accent rounded-lg transition-colors"
                  aria-label="Expand submenu"
                >
                  <motion.div
                    animate={{ rotate: mobileSubMenu === item.target ? 90 : 0 }}
                    transition={{ duration: 0.2 }}
                  >
                    <ChevronRight className="w-4 h-4 text-muted-foreground" />
                  </motion.div>
                </button>
              </div>
              {/* Mobile Nested Submenu - appears under parent */}
              <AnimatePresence>
                {mobileSubMenu === item.target && (
                  <motion.div
                    className="ml-6 border-l-2 border-border/30 pl-3 space-y-1 mt-1"
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.95 }}
                    transition={{ duration: 0.2 }}
                  >
                    {item.subItems.map((subItem, subIndex) => (
                      <motion.button
                        key={subItem.label}
                        onClick={() => onAction(subItem.action, subItem.target)}
                        className="w-full flex items-center gap-3 p-2 rounded-lg hover:bg-accent transition-colors text-left"
                        initial={{ opacity: 0, x: -10 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.1 + (subIndex * 0.05) }}
                      >
                        <div className="w-6 h-6 rounded bg-primary/5 flex items-center justify-center flex-shrink-0">
                          <subItem.icon className="w-3 h-3 text-primary" />
                        </div>
                        <div>
                          <div className="font-medium text-foreground text-xs">
                            {subItem.label}
                          </div>
                          <div className="text-xs text-muted-foreground line-clamp-1">
                            {subItem.description}
                          </div>
                        </div>
                      </motion.button>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </>
          ) : (
            /* Mobile Regular Item */
            <motion.button
              onClick={() => handleItemClick(item)}
              className="w-full flex items-center gap-3 p-3 rounded-lg hover:bg-accent transition-colors text-left"
              initial={{ opacity: 0, x: -10 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.05 }}
            >
              <div className="w-8 h-8 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0">
                <item.icon className="w-4 h-4 text-primary" />
              </div>
              <div>
                <div className="font-medium text-foreground text-sm">
                  {item.label}
                </div>
                <div className="text-xs text-muted-foreground">
                  {item.description}
                </div>
              </div>
            </motion.button>
          )}
        </div>
      ))}
    </div>
  );
}

  // Desktop Component - Wider dropdown with submenu at same level
  return (
    <div 
      className="flex" 
      ref={containerRef}
      style={{ 
        minWidth: '800px', 
        maxWidth: 'min(90vw, 1000px)',
        width: '800px'
      }}
    >
      {/* Main Services Panel */}
      <div className="p-4 flex-1 min-w-96">
        {items.map((item, index) => (
          <div key={item.label} className="relative">
            {item.subItems ? (
  <motion.div
    onMouseEnter={() => handleDesktopItemHover(item)}
    onMouseLeave={handleDesktopItemLeave}
  >
    <motion.button
      onClick={() => onAction("navigate", item.target)}
      className="w-full flex items-start gap-3 p-3 rounded-lg hover:bg-accent transition-colors text-left group"
      initial={{ opacity: 0, x: -10 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: index * 0.05 }}
      whileHover={{ x: 4 }}
    >
      <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0 mt-0.5">
        <item.icon className="w-5 h-5 text-primary" />
      </div>
      <div className="flex-1">
        <div className="font-medium text-foreground flex items-center justify-between">
          <span>{item.label}</span>
          <ChevronRight className="w-3 h-3 opacity-50 group-hover:opacity-100 transition-opacity" />
        </div>
        <div className="text-sm text-muted-foreground mt-0.5 line-clamp-2">
          {item.description}
        </div>
      </div>
    </motion.button>
  </motion.div>
            ) : (
              /* Desktop Regular Item */
              <motion.button
                onClick={() => handleItemClick(item)}
                className="w-full flex items-start gap-3 p-3 rounded-lg hover:bg-accent transition-colors text-left"
                initial={{ opacity: 0, x: -10 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.05 }}
                whileHover={{ x: 4 }}
              >
                <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0 mt-0.5">
                  <item.icon className="w-5 h-5 text-primary" />
                </div>
                <div className="flex-1">
                  <div className="font-medium text-foreground">
                    {item.label}
                  </div>
                  <div className="text-sm text-muted-foreground mt-0.5 line-clamp-2">
                    {item.description}
                  </div>
                </div>
              </motion.button>
            )}
          </div>
        ))}
      </div>

      {/* Desktop Submenu Panel - Same level as main menu */}
      <AnimatePresence>
        {desktopHoveredItem && (
          <motion.div
            className="border-l border-border/50 flex-1 min-w-96 bg-popover/50 backdrop-blur-sm"
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -10 }}
            transition={{ duration: 0.15 }}
            onMouseEnter={() => setDesktopHoveredItem(desktopHoveredItem)}
            onMouseLeave={handleDesktopItemLeave}
          >
            {items.map((item) => {
              if (item.target === desktopHoveredItem && item.subItems) {
                return (
                  <div key={item.target} className="p-4">
                    {item.subItems.map((subItem, subIndex) => (
                      <motion.button
                        key={subItem.label}
                        onClick={() => onAction(subItem.action, subItem.target)}
                        className="w-full flex items-start gap-3 p-3 rounded-lg hover:bg-accent transition-colors text-left focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-1"
                        initial={{ opacity: 0, x: -10 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: subIndex * 0.03 }}
                        whileHover={{ x: 4 }}
                        role="button"
                        tabIndex={0}
                      >
                        <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center flex-shrink-0 mt-0.5">
                          <subItem.icon className="w-5 h-5 text-primary" />
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="font-medium text-foreground truncate">
                            {subItem.label}
                          </div>
                          <div className="text-sm text-muted-foreground mt-0.5 line-clamp-2">
                            {subItem.description}
                          </div>
                        </div>
                      </motion.button>
                    ))}
                  </div>
                );
              }
              return null;
            })}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

