import { createClient } from '@supabase/supabase-js';
import { projectId, publicAnonKey } from './info';

const supabaseUrl = `https://${projectId}.supabase.co`;
const supabase = createClient(supabaseUrl, publicAnonKey);

export async function uploadFile(file: File, folderPath: string = 'uploads'): Promise<string> {
  try {
    const fileExt = file.name.split('.').pop();
    const fileName = `${Date.now()}-${Math.random().toString(36).substring(7)}.${fileExt}`;
    const filePath = `${folderPath}/${fileName}`;

    // Upload to tepa-images bucket (note: this should be a private bucket in production)
    const { data, error } = await supabase.storage
      .from('tepa-images')
      .upload(filePath, file, {
        cacheControl: '31536000',
        upsert: false
      });

    if (error) {
      console.error('File upload error:', error);
      throw new Error(error.message || 'Failed to upload file');
    }

    // Return the file path for admin access (not public URL for security)
    // In production, admins should access files via signed URLs
    return filePath;
  } catch (error) {
    console.error('File upload error:', error);
    throw error;
  }
}

export async function submitContactForm(data: {
  type: 'sales' | 'support' | 'volunteer' | 'event-host' | 'investor';
  name: string;
  email: string;
  company?: string;
  phone?: string;
  message: string;
  subject?: string;
  priority?: 'low' | 'medium' | 'high';
  supportType?: string;
  attachedFiles?: number;
}) {
  try {
    const { error } = await supabase
      .from('contact_submissions')
      .insert([{
        type: data.type,
        name: data.name,
        email: data.email,
        company: data.company,
        phone: data.phone,
        message: data.message,
        subject: data.subject,
        priority: data.priority || 'medium',
        support_type: data.supportType,
        attached_files_count: data.attachedFiles || 0,
        status: 'new',
        submitted_at: new Date().toISOString()
      }]);

    if (error) {
      console.error('Supabase contact form error:', error);
      throw new Error(error.message || 'Failed to submit contact form');
    }

    return { success: true, message: 'Form submitted successfully' };
  } catch (error) {
    console.error('Contact form submission error:', error);
    throw error;
  }
}

