src/components/Forms/ContactForms.tsx.content:
import { useState } from 'react';
import { motion } from 'framer-motion';
import { Phone, Mail, ArrowLeft, Send, CheckCircle, AlertCircle, Upload } from 'lucide-react';
import { submitContactForm, submitVolunteerApplication } from '../../supabase/api';
import { toast } from "sonner";
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Textarea } from '../ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { RadioGroup, RadioGroupItem } from '../ui/radio-group';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '../ui/card';
import { Tepabot } from '../Tepabot';

type FormType = 'sales' | 'support' | 'volunteer' | 'event-host' | 'investor';

interface ContactFormsProps {
  formType: FormType;
  onBack: () => void;
}

const areasOfInterest = [
  'Business Automation',
  'Web Application Development',
  'Mobile App Development',
  'Website Development',
  'SEO Solutions',
  'Custom Integration',
  'Other'
];

const howCanWeHelp = [
  'Quote',
  'Demo',
  'Sales question'
];

const employeeRanges = [
  '1-10',
  '11-50',
  '51-200',
  '201-500',
  '501-1000',
  '1000+'
];

export function ContactForms({ formType, onBack }: ContactFormsProps) {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    jobTitle: '',
    phone: '',
    businessName: '',
    employees: '',
    location: 'Philippines',
    isCurrentClient: '',
    areaOfInterest: '',
    howCanWeHelp: '',
    message: '',
    subject: '' // Added for investor form
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [supportType, setSupportType] = useState('');
  const [attachedFiles, setAttachedFiles] = useState<File[]>([]);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [submissionMessage, setSubmissionMessage] = useState('');

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const files = Array.from(e.target.files);
      setAttachedFiles(prev => [...prev, ...files]);
    }
  };

  const removeFile = (index: number) => {
    setAttachedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (formType === 'volunteer') {
        // Handle volunteer application separately
        await submitVolunteerApplication({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          phone: formData.phone,
          experienceLevel: formData.howCanWeHelp, // Using this field for experience level
          availability: formData.isCurrentClient, // Using this field for availability
          areaOfInterest: formData.areaOfInterest,
          message: formData.message
        });
      } else {
        const submissionData = {
          type: formType,
          name: `${formData.firstName} ${formData.lastName}`.trim(),
          email: formData.email,
          company: formData.businessName,
          phone: formData.phone,
          message: formData.message,
          subject: formType === 'sales' 
            ? `Sales Inquiry - ${formData.areaOfInterest || 'General'}`
            : formType === 'event-host'
            ? `Event Hosting Inquiry - ${formData.areaOfInterest || 'General'}`
            : formType === 'investor'
            ? formData.subject || 'Investor Relations Inquiry'
            : formType === 'support'
            ? `Customer Support Request - ${supportType || 'General'}`
            : 'Customer Support Request',
          priority: formType === 'support' ? 'high' as const : 'medium' as const,
          supportType: supportType,
          attachedFiles: attachedFiles.length
        };

        await submitContactForm(submissionData);
      }

      // Check if this is the landing page (show toast + keep form) or other pages (show contextual message)
      const isLandingPage = window.location.pathname === '/';

      if (isLandingPage) {
        toast.success('Form submitted successfully!', {
          description: 'We will contact you shortly.',
          icon: <CheckCircle className="w-4 h-4" />,
        });
      } else {
        // For non-landing pages, show contextual message and hide form
        setIsSubmitted(true);
        setSubmissionMessage(getSubmissionMessage(formType));
      }

      // Reset form data
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        jobTitle: '',
        phone: '',
        businessName: '',
        employees: '',
        location: 'Philippines',
        isCurrentClient: '',
        areaOfInterest: '',
        howCanWeHelp: '',
        message: '',
        subject: ''
      });
      setSupportType('');
      setAttachedFiles([]);

    } catch (error) {
      console.error('Form submission error:', error);
      toast.error('Failed to submit form', {
        description: 'Please try again or contact us directly.',
        icon: <AlertCircle className="w-4 h-4" />,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const updateFormData = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const getSubmissionMessage = (type: FormType) => {
    switch (type) {
      case 'sales':
        return 'Thank you for your sales inquiry! Our sales team will contact you within 24 hours to discuss your project requirements and provide a detailed quote.';
      case 'support':
        return 'Your support request has been received! Our technical support team will review your issue and respond within 4-6 business hours with a solution.';
      case 'volunteer':
        return 'Thank you for your interest in volunteering with us! We will review your application and contact you within 48 hours to discuss available opportunities.';
      case 'event-host':
        return 'Thank you for your interest in hosting an event with us! Our events team will contact you within 24 hours to discuss your requirements and available dates.';
      case 'investor':
        return 'Thank you for your interest in investment opportunities! Our investor relations team will review your inquiry and respond within 48 hours with relevant information.';
      default:
        return 'Thank you for contacting us! We will get back to you shortly.';
    }
  };

  // Show success message for non-landing page forms
  if (isSubmitted && window.location.pathname !== '/') {
    return (
      <div className="min-h-screen bg-background py-20">
        <div className="container mx-auto px-6 max-w-2xl">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="text-center space-y-6"
          >
            <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto">
              <CheckCircle className="w-10 h-10 text-green-600" />
            </div>
            <h1 className="text-3xl font-bold text-green-800">Thank You!</h1>
            <p className="text-lg text-muted-foreground">{submissionMessage}</p>
            <div className="space-y-2">
              <p className="text-sm text-muted-foreground">
                Need immediate assistance? Contact us directly:
              </p>
              <p className="font-medium">üìû 63 2 8 558 1237</p>
              <p className="font-medium">‚úâÔ∏è hello@tepasolutions.asia</p>
            </div>
            <Button 
              onClick={() => window.location.reload()} 
              variant="outline"
              className="mt-6"
            >
              Submit Another Form
            </Button>
          </motion.div>
        </div>
      </div>
    );
  }

  if (formType === 'sales') {
    return (
      <div className="min-h-screen bg-background py-20">
        <div className="container mx-auto px-6 max-w-4xl">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="space-y-8"
          >
            {/* Header */}
            <div className="text-center space-y-4">
              <Button
                variant="ghost"
                onClick={onBack}
                className="mb-4"
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back
              </Button>

              <h1 className="text-4xl font-bold">Contact Sales</h1>
              <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
                If you would like to have a Tepa Sales representative contact you directly, please submit your details using the form below.
              </p>
            </div>

            {/* Contact Options */}
            <div className="grid md:grid-cols-2 gap-6 mb-8">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Phone className="w-5 h-5 text-primary" />
                    Call Our Sales Team
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-2xl font-bold text-primary">63 2 8 558 1237</p>
                  <p className="text-muted-foreground mt-2">
                    Alternatively, you can call our Sales team directly
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Mail className="w-5 h-5 text-primary" />
                    Other Assistance
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground">
                    If you are looking for other assistance, contact Customer Service.
                  </p>
                  <Button variant="outline" className="mt-3" onClick={() => window.dispatchEvent(new CustomEvent('navigateToService', { detail: 'contact-support' }))}>
                    Customer Service
                  </Button>
                </CardContent>
              </Card>
            </div>

            {/* Talk to Experts Section */}
            <Card>
              <CardHeader>
                <CardTitle>Talk to our experts</CardTitle>
                <CardDescription>
                  Our experts are on hand to discuss your business needs and answer any questions.
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleSubmit} className="space-y-6">
                  {/* How can we help */}
                  <div>
                    <Label className="text-base font-medium">How can we help today?</Label>
                    <RadioGroup
                      value={formData.howCanWeHelp}
                      onValueChange={(value) => updateFormData('howCanWeHelp', value)}
                      className="mt-2"
                    >
                      <div className="flex gap-6">
                        {howCanWeHelp.map((option) => (
                          <div key={option} className="flex items-center space-x-2">
                            <RadioGroupItem value={option} id={option} />
                            <Label htmlFor={option}>{option}</Label>
                          </div>
                        ))}
                      </div>
                    </RadioGroup>
                  </div>

                  {/* Area of Interest */}
                  <div>
                    <Label htmlFor="areaOfInterest" className="text-base font-medium">Area of interest</Label>
                    <Select value={formData.areaOfInterest} onValueChange={(value) => updateFormData('areaOfInterest', value)}>
                      <SelectTrigger className="mt-2">
                        <SelectValue placeholder="Select area of interest" />
                      </SelectTrigger>
                      <SelectContent>
                        {areasOfInterest.map((area) => (
                          <SelectItem key={area} value={area}>{area}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {/* Name Fields */}
                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="firstName" className="text-base font-medium">First Name</Label>
                      <Input
                        id="firstName"
                        value={formData.firstName}
                        onChange={(e) => updateFormData('firstName', e.target.value)}
                        className="mt-2"
                        required
                      />
                    </div>
                    <div>
                      <Label htmlFor="lastName" className="text-base font-medium">Last Name</Label>
                      <Input
                        id="lastName"
                        value={formData.lastName}
                        onChange={(e) => updateFormData('lastName', e.target.value)}
                        className="mt-2"
                        required
                      />
                    </div>
                  </div>

                  {/* Email and Job Title */}
                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="email" className="text-base font-medium">Business Email</Label>
                      <Input
