src/components/OtherComponent/LocalImage.content:
import { getLocalImage, getLocalImagePath } from '../../supabase/local-images';

interface LocalImageProps {
  imageId: string;
  className?: string;
  width?: number;
  height?: number;
  priority?: boolean;
  loading?: 'lazy' | 'eager';
  onClick?: () => void;
  style?: React.CSSProperties;
}

export function LocalImage({ 
  imageId, 
  className = '', 
  width, 
  height, 
  priority = false,
  loading = 'lazy',
  onClick,
  style
}: LocalImageProps) {
  const imageData = getLocalImage(imageId);
  const imagePath = getLocalImagePath(imageId);

  if (!imageData) {
    return (
      <div className={`bg-muted flex items-center justify-center ${className}`}>
        <span className="text-muted-foreground text-sm">Image not found</span>
      </div>
    );
  }

  return (
    <img
      src={imagePath}
      alt={imageData.alt}
      title={imageData.title}
      className={className}
      width={width}
      height={height}
      loading={priority ? 'eager' : loading}
      data-priority={priority ? 'high' : undefined}
      onClick={onClick}
      style={style}
    />
  );
}

src/components/OtherComponent/Logo.tsx.content:
import { motion } from 'framer-motion';

interface LogoProps {
  size?: 'sm' | 'md' | 'lg';
  showText?: boolean;
  onClick?: () => void;
  className?: string;
}

const sizePx = {
  sm: 24,
  md: 32,
  lg: 40
};

export function Logo({ size = 'md', showText = true, onClick, className = '' }: LogoProps) {
  const logoImg = (
    <div className={`flex items-center gap-2 ${onClick ? 'cursor-pointer' : ''} ${className}`}>
      <img
        src="/tepa.png"
        alt="Tepa Solutions Logo"
        width={sizePx[size]}
        height={sizePx[size]}
        className="rounded-lg"
        style={{ objectFit: 'contain' }}
      />
      {showText && (
        <span className={`font-semibold ${size === 'sm' ? 'text-sm' : size === 'lg' ? 'text-xl' : 'text-lg'}`}>
          Tepa Solutions
        </span>
      )}
    </div>
  );

  if (onClick) {
    return (
      <motion.div
        onClick={onClick}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        {logoImg}
      </motion.div>
    );
  }

  return logoImg;
}

src/components/OtherComponent/MobileNavi.content:
import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, ChevronLeft, ChevronRight } from 'lucide-react';
import { Logo } from './Logo';
import { navItems } from '../data/navigation-data';

interface MobileNavigationSheetProps {
  isOpen: boolean;
  onClose: () => void;
  onAction: (action: string, target: string) => void;
}

type NavigationLevel = 'main' | 'what-we-offer' | 'automation' | 'resources' | 'about' | 'contact';

export function MobileNavigationSheet({ isOpen, onClose, onAction }: MobileNavigationSheetProps) {
  const [currentLevel, setCurrentLevel] = useState<NavigationLevel>('main');

  const handleClose = () => {
    setCurrentLevel('main');
    onClose();
  };

  const handleServiceAction = (action: string, target: string) => {
    onAction(action, target);
    handleClose();
  };

  const scrollToSection = (href: string) => {
    if (href === "#") {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else {
      document.querySelector(href)?.scrollIntoView({ behavior: 'smooth' });
    }
    handleClose();
  };

  const renderMainLevel = () => (
    <div className="space-y-2">
      {navItems.map((item) => (
        <div key={item.label}>
          {item.hasDropdown ? (
            <button
              onClick={() => {
                if (item.label === 'What we offer') setCurrentLevel('what-we-offer');
                else if (item.label === 'Resources') setCurrentLevel('resources');
                else if (item.label === 'About Tepa') setCurrentLevel('about');
              }}
              className="w-full flex items-center justify-between p-4 hover:bg-accent rounded-lg transition-colors group"
            >
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                  <item.icon className="w-5 h-5 text-primary" />
                </div>
                <span className="font-medium">{item.label}</span>
              </div>
              <ChevronRight className="w-5 h-5 text-muted-foreground" />
            </button>
          ) : (
            <button
              onClick={() => scrollToSection(item.href)}
              className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
            >
              <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                <item.icon className="w-5 h-5 text-primary" />
              </div>
              <span className="font-medium">{item.label}</span>
            </button>
          )}
        </div>
      ))}
    </div>
  );

  const renderWhatWeOfferLevel = () => {
    const whatWeOfferItem = navItems.find(item => item.label === 'What we offer');
    if (!whatWeOfferItem?.dropdownItems) return null;

    return (
      <div className="space-y-2">
        {whatWeOfferItem.dropdownItems.map((item) => (
          <div key={item.label}>
            {item.action === 'expand' && item.subItems ? (
              <button
                onClick={() => setCurrentLevel('automation')}
                className="w-full flex items-center justify-between p-4 hover:bg-accent rounded-lg transition-colors group"
              >
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                    <item.icon className="w-5 h-5 text-primary" />
                  </div>
                  <div className="text-left">
                    <div className="font-medium">{item.label}</div>
                    <div className="text-sm text-muted-foreground">{item.description}</div>
                  </div>
                </div>
                <ChevronRight className="w-5 h-5 text-muted-foreground" />
              </button>
            ) : (
              <button
                onClick={() => handleServiceAction(item.action, item.target)}
                className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
              >
                <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                  <item.icon className="w-5 h-5 text-primary" />
                </div>
                <div className="text-left">
                  <div className="font-medium">{item.label}</div>
                  <div className="text-sm text-muted-foreground">{item.description}</div>
                </div>
              </button>
            )}
          </div>
        ))}
      </div>
    );
  };

  const renderResourcesLevel = () => {
    const resourcesItem = navItems.find(item => item.label === 'Resources');
    if (!resourcesItem?.dropdownItems) return null;

    return (
      <div className="space-y-2">
        {resourcesItem.dropdownItems.map((item) => (
          <button
            key={item.label}
            onClick={() => handleServiceAction(item.action, item.target)}
            className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
          >
            <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
              <item.icon className="w-5 h-5 text-primary" />
            </div>
            <div className="text-left">
              <div className="font-medium">{item.label}</div>
              <div className="text-sm text-muted-foreground">{item.description}</div>
            </div>
          </button>
        ))}
      </div>
    );
  };

  const renderAboutLevel = () => {
    const aboutItem = navItems.find(item => item.label === 'About Tepa');
    if (!aboutItem?.dropdownItems) return null;

    return (
      <div className="space-y-2">
        {aboutItem.dropdownItems.map((item) => (
          <div key={item.label}>
            {item.action === 'expand' && item.subItems ? (
              <button
                onClick={() => setCurrentLevel('contact')}
                className="w-full flex items-center justify-between p-4 hover:bg-accent rounded-lg transition-colors group"
              >
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                    <item.icon className="w-5 h-5 text-primary" />
                  </div>
                  <div className="text-left">
                    <div className="font-medium">{item.label}</div>
                    <div className="text-sm text-muted-foreground">{item.description}</div>
                  </div>
                </div>
                <ChevronRight className="w-5 h-5 text-muted-foreground" />
              </button>
            ) : (
              <button
                onClick={() => handleServiceAction(item.action, item.target)}
                className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
              >
                <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
                  <item.icon className="w-5 h-5 text-primary" />
                </div>
                <div className="text-left">
                  <div className="font-medium">{item.label}</div>
                  <div className="text-sm text-muted-foreground">{item.description}</div>
                </div>
              </button>
            )}
          </div>
        ))}
      </div>
    );
  };

  const renderContactLevel = () => {
    const aboutItem = navItems.find(item => item.label === 'About Tepa');
    const contactItem = aboutItem?.dropdownItems?.find(item => item.target === 'contact');
    if (!contactItem?.subItems) return null;

    return (
      <div className="space-y-2">
        {contactItem.subItems.map((item) => (
          <button
            key={item.label}
            onClick={() => handleServiceAction(item.action, item.target)}
            className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
          >
            <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
              <item.icon className="w-5 h-5 text-primary" />
            </div>
            <div className="text-left">
              <div className="font-medium">{item.label}</div>
              <div className="text-sm text-muted-foreground">{item.description}</div>
            </div>
          </button>
        ))}
      </div>
    );
  };

  const renderAutomationLevel = () => {
    const whatWeOfferItem = navItems.find(item => item.label === 'What we offer');
    const automationItem = whatWeOfferItem?.dropdownItems?.find(item => item.target === 'automation');
    if (!automationItem?.subItems) return null;

    return (
      <div className="space-y-2">
        {automationItem.subItems.map((item) => (
          <button
            key={item.label}
            onClick={() => handleServiceAction(item.action, item.target)}
            className="w-full flex items-center gap-3 p-4 hover:bg-accent rounded-lg transition-colors group"
          >
            <div className="w-10 h-10 rounded-lg bg-primary/10 flex items-center justify-center group-hover:bg-primary/20 transition-colors">
              <item.icon className="w-5 h-5 text-primary" />
            </div>
            <div className="text-left">
              <div className="font-medium">{item.label}</div>
              <div className="text-sm text-muted-foreground">{item.description}</div>
            </div>
          </button>
        ))}
      </div>
    );
  };

  const getTitle = () => {
    switch (currentLevel) {
      case 'what-we-offer': return 'What we offer';
      case 'automation': return 'Business Automation';
      case 'resources': return 'Resources';
      case 'about': return 'About Tepa';
      case 'contact': return 'Contact Us';
      default: return 'Menu';
    }
  };

  const canGoBack = currentLevel !== 'main';

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            className="fixed inset-0 bg-black/80 z-40 md:hidden"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={handleClose}
          />

          {/* Sheet */}
          <motion.div
            className="fixed top-0 right-0 h-full w-80 max-w-[85vw] bg-background border-l shadow-xl z-50 md:hidden"
            initial={{ x: '100%' }}
            animate={{ x: 0 }}
            exit={{ x: '100%' }}
            transition={{ type: 'spring', damping: 25, stiffness: 200 }}
          >
            <div className="flex flex-col h-full">
              {/* Header */}
              <div className="flex items-center justify-between p-4 border-b">
                <div className="flex items-center gap-3">
                  {canGoBack && (
                    <button
                      onClick={() => {
                        if (currentLevel === 'automation') setCurrentLevel('what-we-offer');
                        else if (currentLevel === 'contact') setCurrentLevel('about');
                        else if (currentLevel === 'what-we-offer' || currentLevel === 'resources' || currentLevel === 'about') setCurrentLevel('main');
                      }}
                      className="p-2 hover:bg-accent rounded-lg transition-colors"
                    >
                      <ChevronLeft className="w-5 h-5" />
                    </button>
                  )}
                  <h2 className="font-semibold">{getTitle()}</h2>
                </div>
                <button
                  onClick={handleClose}
                  className="p-2 hover:bg-accent rounded-lg transition-colors"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>

              {/* Content */}
              <div className="flex-1 overflow-y-auto p-4">
                <AnimatePresence mode="wait">
                  <motion.div
                    key={currentLevel}
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -20 }}
                    transition={{ duration: 0.2 }}
                  >
                    {currentLevel === 'main' && renderMainLevel()}
                    {currentLevel === 'what-we-offer' && renderWhatWeOfferLevel()}
                    {currentLevel === 'automation' && renderAutomationLevel()}
                    {currentLevel === 'resources' && renderResourcesLevel()}
                    {currentLevel === 'about' && renderAboutLevel()}
                    {currentLevel === 'contact' && renderContactLevel()}
                  </motion.div>
                </AnimatePresence>
              </div>

              {/* Footer */}
              <div className="p-4 border-t">
                <div className="flex flex-col items-center gap-2">
                  <Logo size="sm" showText={true} />
                  <p className="text-sm text-muted-foreground">Digital Innovation Partner</p>
                </div>
              </div>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
}

src/components/OtherComponent/Portfolio..content:
import { motion, useInView } from 'framer-motion';
import { Badge } from '../ui/badge';
import { Button } from '../ui/button';
import { ExternalLink, Github, Play } from 'lucide-react';
import { projects } from '../data/projects-data';
import { FoodDeliveryDemo } from '../demos/FoodDeliveryDemo';
import { AdminDashboardDemo } from '../demos/AdminDashboardDemo';
import { DentalWebsiteDemo } from '../demos/DentalWebsiteDemo';
import { WorkflowAutomationDemo } from '../demos/WorkflowAutomationDemo';
import { SEODemo } from '../demos/SEODemo';
import { useRef, useState } from 'react';

const demoComponents = {
  FoodDeliveryDemo,
  AdminDashboardDemo,
  DentalWebsiteDemo,
  WorkflowAutomationDemo,
  SEODemo
};

// Mapping between demo component and service page
const demoToServiceMap: { [key: string]: string } = {
  'FoodDeliveryDemo': 'app-dev',
  'AdminDashboardDemo': 'web-app', 
  'DentalWebsiteDemo': 'web-dev',
  'SEODemo': 'seo'
};

export function Portfolio() {
  const [liveDemoStates, setLiveDemoStates] = useState<{ [key: string]: boolean }>({});

  const toggleLiveDemo = (projectId: string) => {
    setLiveDemoStates(prev => ({
      ...prev,
      [projectId]: !prev[projectId]
    }));
  };

  return (
    <section id="portfolio" className="py-24 bg-gradient-to-b from-background to-muted/30">
      <div className="container mx-auto px-6">
        <motion.div
          className="text-center mb-16"
          initial={{ y: 50, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <Badge className="mb-4">Demos</Badge>
          <h2 className="text-3xl md:text-4xl lg:text-5xl mb-4">
            What We Offer
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Browse our demos to get a feel for what we do. These examples show the kind of apps and solutions we build.
          </p>
        </motion.div>

        <div className="space-y-32">
          {projects.map((project, index) => (
            <ProjectDemo 
              key={project.id} 
              project={project} 
              index={index} 
              isLiveDemo={liveDemoStates[project.id] || false}
              onToggleLiveDemo={() => toggleLiveDemo(project.id)}
            />
          ))}
        </div>


      </div>
    </section>
  );
}

function ProjectDemo({ 
  project, 
  index, 
  isLiveDemo, 
  onToggleLiveDemo 
}: { 
  project: any; 
  index: number; 
  isLiveDemo: boolean;
  onToggleLiveDemo: () => void;
}) {
  const ref = useRef(null);
  const isInView = useInView(ref, { 
    once: false, 
    margin: "-20% 0px -20% 0px" 
  });

  const DemoComponent = demoComponents[project.demoComponent as keyof typeof demoComponents];
  const isReversed = index % 2 === 1;

  return (
    <motion.div
      ref={ref}
      className="min-h-screen flex items-center py-16"
      initial={{ opacity: 0 }}
      whileInView={{ opacity: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 0.8 }}
    >
      <div className={`grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-16 items-center w-full max-w-7xl mx-auto ${
        isReversed ? 'lg:grid-flow-col-dense' : ''
      } ${index >= 1 ? 'px-5' : ''}`}>
        {/* Demo Container */}
        <motion.div
          className={`flex justify-center ${isReversed ? 'lg:col-start-2' : ''}`}
          initial={{ x: isReversed ? 100 : -100, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <div className="relative">
            {/* Device Frame for Mobile Apps */}
            {project.category === 'mobile' ? (
              <div className="relative scale-75 sm:scale-90 lg:scale-100">
                <DemoComponent isVisible={isInView} isLiveDemo={isLiveDemo} />
                {/* Phone Shadow */}
                <div className="absolute -inset-4 bg-gradient-to-r from-black/20 to-transparent rounded-[3rem] blur-xl -z-10" />
              </div>
            ) : project.category === 'automation' ? (
              /* Automation Flow Frame */
              <div className="relative scale-50 sm:scale-75 lg:scale-90 xl:scale-100">
                <DemoComponent isVisible={isInView} />
                {/* Automation Shadow */}
                <div className="absolute -inset-8 bg-gradient-to-r from-indigo-500/20 to-purple-500/20 rounded-xl blur-2xl -z-10" />
              </div>
            ) : project.category === 'seo' ? (
              /* SEO Demo Frame */
              <div className="relative scale-75 sm:scale-90 lg:scale-100">
                <DemoComponent isVisible={isInView} isLiveDemo={isLiveDemo} />
                {/* SEO Shadow */}
                <div className="absolute -inset-4 bg-gradient-to-r from-green-500/20 to-emerald-500/20 rounded-xl blur-xl -z-10" />
              </div>
            ) : (
              /* Desktop Browser Frame */
              <div className="relative scale-50 sm:scale-75 lg:scale-90 xl:scale-100">
                <DemoComponent isVisible={isInView} isLiveDemo={isLiveDemo} />
                {/* Desktop Shadow */}
                <div className="absolute -inset-8 bg-gradient-to-r from-black/20 to-transparent rounded-xl blur-2xl -z-10" />
              </div>
            )}
          </div>
        </motion.div>

        {/* Project Info */}
        <motion.div
          className={`space-y-6 px-4 lg:px-0 ${isReversed ? 'lg:col-start-1' : ''}`}
          initial={{ x: isReversed ? -100 : 100, opacity: 0 }}
          whileInView={{ x: 0, opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <div>
            <Badge className="mb-4" variant="outline">
              {project.type}
            </Badge>
            <h3 className="text-2xl md:text-3xl lg:text-4xl font-bold mb-4">
              {project.title}
            </h3>
            <p className="text-base lg:text-lg text-muted-foreground leading-relaxed">
              {project.description}
