                  Explore SEO Solutions
                </Button>
              ) : (
                <>
                  <Button 
                    className="group"
                    onClick={onToggleLiveDemo}
                    variant={isLiveDemo ? "outline" : "default"}
                  >
                    {isLiveDemo ? (
                      <>
                        <ExternalLink className="size-4 mr-2" />
                        Exit Live Demo
                      </>
                    ) : (
                      <>
                        <Play className="size-4 mr-2" />
                        Try Live Demo
                      </>
                    )}
